package Allprogram;

public class IfOddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while(i<=100)
		{
			if(i%2==0)
			{
				System.out.println("it is even number " +i);
			}
			else
			{
				System.out.println("it is odd number " +i);
			}
		i=i+1;
		}

	}

}
